define({"topics" : [{"title":"DITA-OT architecture","shortdesc":"<p class=\"shortdesc\">The DITA Open Toolkit is an open-source implementation of the OASIS specification for the Darwin     Information Typing Architecture. The toolkit uses Ant, XSLT, and Java to transform DITA content (maps and     topics) into different deliverable formats.</p>","href":"reference\/architecture.html","attributes": {"data-id":"ditaotarch",},"menu": {"hasChildren":true,},"tocID":"ditaotarch-d50e2905","next":"ditaotarch-d50e2905",},{"title":"DITA specification support","shortdesc":"<p class=\"shortdesc\">DITA Open Toolkit <span class=\"keyword\">3.1</span> supports all versions of the OASIS DITA specification,     including 1.0, 1.1, 1.2, and 1.3.</p>","href":"reference\/DITA_spec-support.html","attributes": {"data-id":"dita-spec-support",},"menu": {"hasChildren":true,},"tocID":"dita-spec-support-d50e3693","next":"dita-spec-support-d50e3693",},{"title":"Java API","shortdesc":"<p class=\"shortdesc\">DITA-OT includes a Java Application Programming Interface to allow developers to embed DITA-OT more easily     into other Java programs.</p>","href":"reference\/java-api.html","attributes": {"data-id":"API",},"menu": {"hasChildren":false,},"tocID":"API-d50e3829","topics":[]},{"title":"License","shortdesc":"<p class=\"shortdesc\">     <span class=\"ph\">DITA Open Toolkit is released under the Apache License, Version 2.0.</span>   </p>","href":"reference\/license.html","attributes": {"data-id":"ID",},"menu": {"hasChildren":true,},"tocID":"ID-d50e3850","next":"ID-d50e3850",}]});